// SET UP SCHEDULE
let arenas = [];
for(let i = 0; i < teams.length / 2; i++) {
    let newArena = new Arena(i);
    arenas.push(newArena);
}

assignMatchups();

function assignMatchups() {
    for (let iterator = 0; iterator < 150; iterator++) {
        let teamsCopy = shuffleArray(teams);
        let currArena = 0;
        while (teamsCopy.length > 0) {
            let tempTeam = teamsCopy.shift();
            let tempTeam2 = findOpponent(tempTeam, teamsCopy);
            arenas[currArena].matchups[iterator] = new TeamMatchup(tempTeam, tempTeam2);
            tempTeam.schedule[iterator] = tempTeam2;
            tempTeam2.schedule[iterator] = tempTeam;
            teamsCopy.splice(teamsCopy.indexOf(tempTeam2), 1);
            currArena++;
        }
    }
}

function findOpponent(team, teams) {
    if(teams.length > 0) {
        teams.sort(function(a, b){return countMatchesAgainst(team, a) - countMatchesAgainst(team, b)});
    return teams[0];
    }
    return null;
}

function countMatchesAgainst(team1, team2) {
    let counter = 0;
    team1.schedule.forEach(t => {
        if (t.name == team2.name) {
            counter++;
        }
    });
    return counter;
}

